package info.nukoneko.android.qritter.util;import android.app.Service;import android.content.Context;import android.net.ConnectivityManager;import android.net.NetworkInfo;import android.support.annotation.LayoutRes;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.view.LayoutInflater;import android.view.View;import info.nukoneko.android.qritter.AppController;/** * Created by TEJNEK on 2016/03/17. */public class NKUtil {    public static Boolean isConnection(Context context) {        ConnectivityManager cm = (ConnectivityManager) context.getSystemService(Context.CONNECTIVITY_SERVICE);        NetworkInfo info = cm.getActiveNetworkInfo();        return info != null && info.isConnected();    }    @Nullable    public static View getInflateView(@NonNull Context context, @LayoutRes int resourceID) {        try {            return LayoutInflater.from(context).inflate(resourceID, null);        }catch (Exception e){            return null;        }    }    @Nullable    public static View getInflateView(@LayoutRes int resourceID) {        Context context = AppController.get().getApplicationContext();        try {            LayoutInflater inflater = (LayoutInflater) context.getSystemService(Service.LAYOUT_INFLATER_SERVICE);            return inflater.inflate(resourceID, null);        }catch (Exception e){            return null;        }    }}