package info.nukoneko.android.qritter.util.tools;import android.content.Context;import android.support.annotation.StringRes;import android.view.Gravity;import android.widget.TextView;import android.widget.Toast;import info.nukoneko.android.qritter.AppController;import info.nukoneko.android.qritter.R;import info.nukoneko.android.qritter.util.NKUtil;/** * Created by TEJNEK on 2016/03/17. */public class ShowToast {    public static void showToast(String text, int duration){        Context context = AppController.get().getApplicationContext();        try{            TextView view = (TextView) NKUtil.getInflateView(R.layout.item_textview_corner_rect);            if(view == null) throw new IllegalArgumentException();            view.setGravity(Gravity.CENTER_HORIZONTAL);            view.setText(text);            Toast toast = new Toast(context);            toast.setView(view);            toast.setDuration(duration);            toast.show();        }catch (Exception e){            try{                Toast.makeText(context, text, Toast.LENGTH_SHORT).show();            }catch (Exception ignore){            }        }    }    public static void showToast(@StringRes int textID){        Context context = AppController.get().getApplicationContext();        showToast(context.getString(textID), Toast.LENGTH_SHORT);    }    public static void showToast(String text){        showToast(text, Toast.LENGTH_SHORT);    }}